@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
  --font-sans: 'DM Sans', sans-serif;
}

:root {
  --radius: 0.5rem;
  --background: hsl(45 30% 97%);
  --foreground: hsl(150 15% 20%);
  --card: hsl(0 0% 100%);
  --card-foreground: hsl(150 15% 20%);
  --popover: hsl(0 0% 100%);
  --popover-foreground: hsl(150 15% 20%);
  --primary: hsl(150 35% 45%);
  --primary-foreground: hsl(0 0% 100%);
  --secondary: hsl(45 20% 94%);
  --secondary-foreground: hsl(150 15% 30%);
  --muted: hsl(45 20% 94%);
  --muted-foreground: hsl(150 10% 45%);
  --accent: hsl(150 30% 92%);
  --accent-foreground: hsl(150 35% 30%);
  --destructive: hsl(10 60% 55%);
  --border: hsl(150 15% 88%);
  --input: hsl(150 15% 88%);
  --ring: hsl(150 35% 45%);
  --chart-1: hsl(150 45% 45%);
  --chart-2: hsl(40 80% 55%);
  --chart-3: hsl(10 60% 55%);
  --chart-4: hsl(150 30% 60%);
  --chart-5: hsl(45 40% 70%);
  --sidebar: hsl(0 0% 100%);
  --sidebar-foreground: hsl(150 15% 20%);
  --sidebar-primary: hsl(150 35% 45%);
  --sidebar-primary-foreground: hsl(0 0% 100%);
  --sidebar-accent: hsl(150 30% 92%);
  --sidebar-accent-foreground: hsl(150 35% 30%);
  --sidebar-border: hsl(150 15% 88%);
  --sidebar-ring: hsl(150 35% 45%);
}

.dark {
  --background: hsl(150 15% 10%);
  --foreground: hsl(45 30% 95%);
  --card: hsl(150 15% 15%);
  --card-foreground: hsl(45 30% 95%);
  --popover: hsl(150 15% 15%);
  --popover-foreground: hsl(45 30% 95%);
  --primary: hsl(150 35% 50%);
  --primary-foreground: hsl(150 15% 10%);
  --secondary: hsl(150 10% 20%);
  --secondary-foreground: hsl(45 30% 95%);
  --muted: hsl(150 10% 20%);
  --muted-foreground: hsl(150 10% 60%);
  --accent: hsl(150 20% 25%);
  --accent-foreground: hsl(45 30% 95%);
  --destructive: hsl(10 60% 50%);
  --border: hsl(150 10% 25%);
  --input: hsl(150 10% 25%);
  --ring: hsl(150 35% 50%);
  --chart-1: hsl(150 45% 50%);
  --chart-2: hsl(40 80% 60%);
  --chart-3: hsl(10 60% 50%);
  --chart-4: hsl(150 30% 55%);
  --chart-5: hsl(45 40% 65%);
  --sidebar: hsl(150 15% 15%);
  --sidebar-foreground: hsl(45 30% 95%);
  --sidebar-primary: hsl(150 35% 50%);
  --sidebar-primary-foreground: hsl(150 15% 10%);
  --sidebar-accent: hsl(150 20% 25%);
  --sidebar-accent-foreground: hsl(45 30% 95%);
  --sidebar-border: hsl(150 10% 25%);
  --sidebar-ring: hsl(150 35% 50%);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground font-sans;
  }
}

/* Custom animation for wellness arc */
@keyframes arc-fill {
  from {
    stroke-dashoffset: 283;
  }
}

.animate-arc {
  animation: arc-fill 0.8s ease-out forwards;
}

/* Stagger fade-in animations */
@keyframes fade-up {
  from {
    opacity: 0;
    transform: translateY(8px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.animate-fade-up {
  animation: fade-up 0.3s ease-out forwards;
}

.animation-delay-100 {
  animation-delay: 100ms;
  opacity: 0;
}

.animation-delay-200 {
  animation-delay: 200ms;
  opacity: 0;
}

.animation-delay-300 {
  animation-delay: 300ms;
  opacity: 0;
}

