          </div>
        </div>

        {/* Entry Detail Sheet for Desktop */}
        <EntryDetailSheet
          entry={selectedEntry}
          open={entryDetailOpen}
          onOpenChange={setEntryDetailOpen}
          onDelete={handleDeleteEntry}
          onEdit={() => {
            setEntryDetailOpen(false);
          }}
        />
      </div>
    </div>
  );
}

